#!/usr/bin/env node
const yargs = require("yargs");
const { colors, printBanner, clearScreen } = require("./console-utils");

function printHelp() {
  printBanner();
  console.log(`
  Tools:
    [-] ${colors.FgGreen}bcw-create:${colors.Reset} creates a new project from templates
    [-] ${colors.FgGreen}bcw-add:${colors.Reset} selectively add packages to your existing project
    [-] ${colors.FgGreen}bcw-serve (-p || --port):${colors.Reset} initailizes the static server with optional specification for the port to serve on
    [-] ${colors.FgGreen}bcw-update:${colors.Reset} installs the latest version of the bcw tool`);
}

function start(){
  try {
    clearScreen();
    printBanner();
    const commands = require("yargs")
    commands.usage("bcw [command] [options]")
      .command("setup", "Setup and configure environment for classroom", yargs => {
        require('./setup');
      })
      .command("create", "creates a new project from templates", yargs => {
        require('./create');
      })
      .command("journal", "setup and create the journal project", yargs => {
        require('./journals');
      })
      .command("add", "adds packages to your existing project", yargs => {
        require('./add');
      })
      .command("serve", "initializes the static server with optional specification for the port to serve on", yargs => {
        require('./serve-static');
      })
      .command("update", "installs the latest version of the bcw tool", yargs => {
        require('./check-update');
      })
      .command("help", 'Prints the help menu', yargs => {
        printHelp();
      })
      .alias("h", "help")
      .alias("v", "version")
      .demandCommand(1, "You must specify a command")
      .version()
      .argv;
  } catch (error) {
    console.error(error);
  }
}

start()
